import React from 'react';
import './Home.css';
import "antd/dist/antd.css";
import IMG from './news.png'
import { Layout, Typography, Card, Col, Row, Button, Input, Space, List } from 'antd';


const { Header, Content, Footer } = Layout;
const { TextArea } = Input;
const { Title } = Typography;
const { Meta } = Card;
const backendUrl = "http://127.0.0.1:5000/predict?sentence=";

export async function getNews() {
  let result = await fetch("http://127.0.0.1:5000/news").then(response => response.json());
  console.log(result)
  return result;
}

class About extends React.Component {

  constructor(props) {
    super(props);
    this.state = { articles: [], _isMounted: false};
    this.fetchNews = this.fetchNews.bind(this);
    this.posDict = {};
    this.negDict = {};
  }

  componentDidMount() {
    this.fetchNews();
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  fetchNews() {
    getNews()
      .then(news => this.setState({ articles:news}));
  }

  predictSentiment(news,index){
    var posInput = document.getElementById("positiveInput"+index);
    var negInput = document.getElementById("negativeInput"+index);    
    fetch(backendUrl+news)
    .then(res => res.json())
    .then((data) => { 
      this.setState({ posDict: data })
      this.posDict[index] = data['response']['positive'];
      this.negDict[index] = data['response']['negative'];
      posInput.value = this.posDict[index];
      negInput.value = this.negDict[index];
    })
    .catch(console.log);
  }

  render() {
    return (
    <Layout>
    <Layout className="site-layout" style={{ marginLeft: 200 }}>
      <Header className="site-layout-background" style={{ padding: 0 }}> 
      <Title level={3} >
        <div style={{ textAlign: 'center', marginTop: '1%' }}>Latest News Headlines about</div>
      </Title>
      </Header>
      <Content style={{ margin: '24px 16px 0', overflow: 'initial' }}>
        <div className="site-layout-background" style={{ padding: 24, textAlign: 'center' }}>
        <Row>
          {/* <FlatList
          data={this.state.articles}
          renderItem={({ item }) => <Article article={item} />}
          keyExtractor={item => item.url}
          refreshing={this.state.refreshing}
          onRefresh={this.handleRefresh.bind(this)}/> */}
          <List
            grid={{ gutter: 16, column: 3 }}
            dataSource={this.state.articles}
            renderItem={(item,index) => (
            <List.Item>
              <Card hoverable style={{ width: 360, height: 400 }} cover={<img alt="img" src={item.metadata.meta_img || IMG}/>}>
                <Meta title={item.title} description={item.content + '\n' + item.url || 'Read More..'} />
              </Card>
            </List.Item>
          )}/> 
        </Row>
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>An AI to share ‘Positive’ Stories ©2020 MSc Individual Project</Footer>
    </Layout>
  </Layout>
    );
  }
}



export default About;
