import React from 'react';
import './About.css';
import "antd/dist/antd.css";
import IMG from './news.png'
import { Layout, Typography, Card, Row, Input, List, Select } from 'antd';

const { Option } = Select;
const { Header, Content, Footer } = Layout;
const { TextArea } = Input;
const { Title } = Typography;
const { Meta } = Card;
const backendUrl = "http://127.0.0.1:5000/predict?sentence=";

export async function getNews(outlet) {
  let result = await fetch("http://127.0.0.1:5000/news?outlet=" + outlet).then(response => response.json());
  return result;
}

class About extends React.Component {

  constructor(props) {
    super(props);
    this.state = { articles: [], _isMounted: false, outlet: "BBC" };
    this.fetchNews = this.fetchNews.bind(this);
    this.posDict = {};
    this.negDict = {};
  }

  componentDidMount() {
    this.fetchNews();
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  fetchNews() {
    getNews(this.state.outlet)
      .then(news => this.setState({ articles: news }));
  }

  predictSentiment(news, index) {
    var posInput = document.getElementById("positiveInput" + index);
    var negInput = document.getElementById("negativeInput" + index);
    fetch(backendUrl + news)
      .then(res => res.json())
      .then((data) => {
        this.setState({ posDict: data })
        this.posDict[index] = data['response']['positive'];
        this.negDict[index] = data['response']['negative'];
        posInput.value = this.posDict[index];
        negInput.value = this.negDict[index];
      })
      .catch(console.log);
  }

  onChange(value) {
    console.log(value)
    this.setState({outlet: value})
    console.log('new: ' + this.state.outlet)
  }

  render() {
    return (
      <Layout>
        <Layout className="site-layout" style={{ marginLeft: 200 }}>
          <Header className="site-layout-background" style={{ padding: 0 }}>
            <Title level={3} >
              <div style={{ textAlign: 'center', marginTop: '1%' }}>Latest News Headlines about</div>
            </Title>
          </Header>
          <Content style={{ margin: '24px 16px 0', overflow: 'initial' }}>
            <div className="site-layout-background" style={{ padding: 24, textAlign: 'center' }}>
              <Row style={{ marginBottom: 20 }}>
                <div style={{ width: 200, fontWeight:'bold', textAlign: 'center', fontSize:'17px'}}> Select a news outlet: </div>
                <Select
                  defaultValue="BBC"
                  showSearch
                  style={{ width: 200 }}
                  optionFilterProp="children"
                  onChange={() => this.onChange(this.option.value)}
                  filterOption={(input, option) =>
                    option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                  }
                >
                  <Option value="BBC">BBC</Option>
                  <Option value="DailyMail">DailyMail</Option>
                  <Option value="Guardian">Guardian</Option>
                  <Option value="Metro">Metro</Option>
                  <Option value="Mirror">Mirror</Option>
                  <Option value="Reuters">Reuters</Option>
                  <Option value="Sun">Sun</Option>
                  <Option value="Independent">Independent</Option>
                </Select>
              </Row>
              <Row>
                <List
                  grid={{ gutter: 16, column: 3 }}
                  dataSource={this.state.articles}
                  renderItem={(item, index) => (
                    <List.Item>
                      <Card hoverable style={{ width: 310, height: 400 }}
                        cover={
                          <a href={item.url} >
                            <img style={{ maxWidth: '100%', maxHeight: '100%' }} alt="img" src={item.metadata.meta_img || IMG} />
                          </a>
                        }
                      >
                        <Meta
                          title={[
                            <TextArea className="news-title" rows={2} value={item.title} style={{ fontWeight: 'bold', resize: 'none', border: 'none', overflow: 'overlay' }} />
                          ]}
                          description={[
                            <TextArea className="news-content" rows={6} value={item.content + '\n' + item.url || 'Read More..'} style={{ resize: 'none', width: '101%', overflowY: 'overlay' }} />,
                          ]}
                        />
                      </Card>
                    </List.Item>
                  )} />
              </Row>
            </div>
          </Content>
          <Footer style={{ textAlign: 'center' }}>An AI to share ‘Positive’ Stories ©2020 MSc Individual Project</Footer>
        </Layout>
      </Layout>
    );
  }
}



export default About;
