import React from 'react';
import './News.css';
import "antd/dist/antd.css";
import IMG from './news.png'
import { Layout, Typography, Col, Row, Button, Input, Space, List, Select } from 'antd';

const { Option } = Select;
const { Header, Content, Footer } = Layout;
const { TextArea } = Input;
const { Title } = Typography;
const backendUrl = "http://127.0.0.1:5000/upload?url=";


class Upload extends React.Component {

  constructor(props) {
    super(props);
    this.state = { article:none, score:-1, prediction:"", title: "", content: "", url:"", image_url:"" };
  }

  predictSentiment(news,index){
    var posInput = document.getElementById("positiveInput"+index);
    // var negInput = document.getElementById("negativeInput"+index);    
    fetch(backendUrl+news)
    .then(res => res.json())
    .then((data) => { 
      this.setState({ posDict: data })
      this.posDict[index] = data['response']['positive'];
      // this.negDict[index] = data['response']['negative'];
      posInput.value = this.posDict[index];
      // negInput.value = this.negDict[index];
    })
    .catch(console.log);
  }

  uploadNews(){
    var input_url = document.getElementById("input").value;
    this.setState({ url: input_url })
    fetch(backendUrl+input_url)
    .then(res => res.json())
    .then((data) => { 
      this.setState({ article: data['response']['article'], score:data['response']['score']})
      this.posDict[index] = data['response']['positive'];

    })
    .catch(console.log);
  }


  render() {
    return (
    <Layout>
    <Layout className="site-layout" style={{ marginLeft: 200 }}>
      <Header className="site-layout-background" style={{ padding: 0 }}> 
      <Title level={3} >
        <div style={{ textAlign: 'center', marginTop: '1%' }}>Upload News</div>
      </Title>
      </Header>
      <Content style={{ margin: '24px 16px 0', overflow: 'initial' }}>
        <div className="site-layout-background" style={{ padding: 24, textAlign: 'center' }}>
        <Row>
        <Col span={12}>
          <Input id="input" placeholder="Input URL here" />
        </Col>
        <Col span={6}>
        <Button type="primary" id="upload" onClick={()=> this.uploadNews()}>Upload</Button>
        </Col>
        </Row>
        <Row style={{marginTop: 20}}>
          <Col span={8}>
          <div style={{height: 187}}>
          <a href={this.state.url} ><img style={{width:'auto', height:'auto', maxWidth: '100%', maxHeight: '100%'}} alt="img" src={this.state.image_url || IMG} /></a>
          </div>
          </Col>
          <Col span={10}>
          <TextArea className="news-content" rows={8} id={"prediction"} value={this.prediction} style={{ resize:'none', overflow: 'overlay' }}/>
          </Col>
        </Row>
        <Row style={{marginTop: 5}}>
          <Col span={18}><TextArea placeholder="title" className="news-title" rows={1} value={this.state.title} style={{ fontWeight:'bold',resize:'none', overflow: 'overlay' }}/></Col>
        </Row>
        <Row>
          <Col span={18}><TextArea placeholder="body" className="news-content" rows={10} value= {this.state.content} style={{resize:'none', overflowY: 'overlay' }}/></Col>
        </Row>
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>An AI to share ‘Positive’ Stories ©2020 MSc Individual Project</Footer>
    </Layout>
  </Layout>
    );
  }
}

export default Upload;
